script:
frappe.ui.form.on('Development Project', {
    estimated_total_cost: function(frm) {
        calculate_cost_per_lot(frm);
    },
    residential_area_sqm: function(frm) {
        calculate_cost_per_lot(frm);
    },
    residential_lots: function(frm) {
        calculate_cost_per_lot(frm);
    },
    refresh: function(frm) {
        // חישוב ראשוני כשהטופס נטען
        calculate_cost_per_lot(frm);
    }
});

function calculate_cost_per_lot(frm) {
    let total_cost = frm.doc.estimated_total_cost || 0;
    let area_sqm = frm.doc.residential_area_sqm || 0;
    let lots = frm.doc.residential_lots || 0;

    if (lots > 0 && area_sqm > 0 && total_cost > 0) {
        // חישוב גודל מגרש ממוצע
        let avg_lot_size = area_sqm / lots;

        // חישוב עלות למ"ר
        let cost_per_sqm = total_cost / area_sqm;

        // חישוב עלות למגרש ממוצע = עלות למ"ר × גודל מגרש ממוצע
        let cost_per_lot = cost_per_sqm * avg_lot_size;

        // עדכון השדה
        frm.set_value('cost_per_lot', cost_per_lot);

        console.log('Cost Per Lot Calculation (Fixed):', {
            total_cost: total_cost,
            area_sqm: area_sqm,
            lots: lots,
            avg_lot_size: avg_lot_size.toFixed(2),
            cost_per_sqm: cost_per_sqm.toFixed(2),
            cost_per_lot: cost_per_lot.toFixed(2)
        });
    } else {
        frm.set_value('cost_per_lot', 0);
    }
}

frappe.ui.form.on('Arrangement File', {
    assigned_lot: function(frm) {
        if (frm.doc.assigned_lot) {
            // שליפת נתוני המגרש
            frappe.db.get_doc('Lot', frm.doc.assigned_lot).then(lot => {
                if (lot.area_sqm && lot.cost_per_sqm) {
                    // חישוב מחיר המגרש
                    let lot_price = lot.area_sqm * lot.cost_per_sqm;
                    frm.set_value('lot_price', lot_price);

                    // עדכון סטטוס הפיתוח
                    if (lot.development_status) {
                        frm.set_value('lot_development_status', lot.development_status);
                    }
                }
            });
        } else {
            // ניקוי שדות כאשר לא נבחר מגרש
            frm.set_value('lot_price', 0);
            frm.set_value('lot_development_status', '');
        }
    },

    // חישוב מחדש כאשר נטענת הטופס
    refresh: function(frm) {
        if (frm.doc.assigned_lot) {
            // הפעלת החישוב מחדש
            frm.trigger('assigned_lot');
        }
    }
});

frappe.ui.form.on('Lot', {
    related_project: function(frm) {
        if (frm.doc.related_project) {
            // שליפת נתוני פרויקט הפיתוח
            frappe.db.get_doc('Development Project', frm.doc.related_project).then(project => {
                // עדכון סטטוס הפיתוח
                frm.set_value('development_status', project.status);

                // עדכון עלות למטר רבוע
                if (project.estimated_cost_per_sqm) {
                    frm.set_value('cost_per_sqm', project.estimated_cost_per_sqm);
                }
            }).catch(error => {
                console.log('Error fetching development project:', error);
            });
        } else {
            // ניקוי שדות כאשר לא נבחר פרויקט
            frm.set_value('development_status', '');
            frm.set_value('cost_per_sqm', 0);
        }
    },

    // עדכון כאשר נטענת הטופס
    refresh: function(frm) {
        if (frm.doc.related_project) {
            frm.trigger('related_project');
        }
    },

    // עדכון כאשר נטענת התוכנית משדה Plan
    plan: function(frm) {
        if (frm.doc.plan) {
            // שליפת פרויקט הפיתוח מהתוכנית
            frappe.db.get_value('Plan', frm.doc.plan, 'development_project').then(response => {
                if (response.message && response.message.development_project) {
                    frm.set_value('related_project', response.message.development_project);
                }
            });
        }
    }
});

frappe.ui.form.on('Development Project', {
  onload: function(frm) {
    frm.set_query('task', 'main_tasks', function() {
      return {
        filters: {
          development_project: frm.doc.name,
          parent_development_task: ["is", "not set"]
        }
      };
    });
  }
});

frappe.ui.form.on('Lot', {
    lot_number: function(frm) {
        set_lot_id(frm);
    },
    plan: function(frm) {
        set_lot_id(frm);
    },
    plan_number: function(frm) {
        set_lot_id(frm);
    }
});

function set_lot_id(frm) {
    if (frm.doc.lot_number && frm.doc.plan_number) {
        frm.set_value('lot_id', `${frm.doc.lot_number}@${frm.doc.plan_number}`);
    }
}

frappe.ui.form.on('Evacuee', {
    first_name: function(frm) {
        set_full_name(frm);
    },
    last_name: function(frm) {
        set_full_name(frm);
    }
});

function set_full_name(frm) {
    if (frm.doc.first_name && frm.doc.last_name) {
        frm.set_value('full_name', `${frm.doc.first_name} ${frm.doc.last_name}`);
    }
}

frappe.ui.form.on('Fixture Compensation', {
    fixture_compensation_add: function(frm) {
        calculate_total_fixture_compensation(frm);
    },
    fixture_compensation_remove: function(frm) {
        calculate_total_fixture_compensation(frm);
    },
    compensation_amount: function(frm) {
        calculate_total_fixture_compensation(frm);
    },
    quantity: function(frm) {
        calculate_total_fixture_compensation(frm);
    }
});

function calculate_total_fixture_compensation(frm) {
    let total = 0;
    (frm.doc.fixture_compensation || []).forEach(row => {
        if (row.compensation_amount) {
            total += row.compensation_amount;
        }
    });
    frm.set_value('total_fixture_compensation', total);
}

frappe.ui.form.on('Development Project', {
    refresh(frm) {
        if (frm.doc.name) {
            // כפתור לעבור לרשימת משימות מסוננת
            frm.add_custom_button('📋 רשימת משימות לפרויקט', () => {
                frappe.set_route("List", "Development Task", {
                    development_project: frm.doc.name
                });
            });

            // כפתור לעץ משימות (Tree View) מסונן לפי פרויקט
            frm.add_custom_button('🌳 עץ המשימות לפרויקט', () => {
                frappe.set_route("Tree", "Development Task", {
                    development_project: frm.doc.name
                });
            });
        }
    }
});

frappe.ui.form.on('Plan', {
    refresh: function(frm) {
        // Add custom button to refresh lots summary
        if (!frm.is_new()) {
            // Add button to the main toolbar (more visible)
            frm.add_custom_button(__('🔄 Refresh Lots Summary'), function() {
                // Show loading indicator
                frappe.show_alert({
                    message: __('Calculating lots summary...'),
                    indicator: 'blue'
                });

                frappe.call({
                    method: 'frappe.client.get_list',
                    args: {
                        doctype: 'Lot',
                        filters: {'plan': frm.doc.name},
                        fields: ['area_sqm']
                    },
                    callback: function(r) {
                        if (r.message) {
                            let lots = r.message;
                            let total_lots = lots.length;
                            let total_area = lots.reduce((sum, lot) => sum + (lot.area_sqm || 0), 0);
                            let residential_lots = lots.filter(lot => (lot.area_sqm || 0) > 0).length;
                            let residential_area = lots.filter(lot => (lot.area_sqm || 0) > 0).reduce((sum, lot) => sum + lot.area_sqm, 0);

                            // Update form fields
                            frm.set_value('total_lots', total_lots);
                            frm.set_value('total_area_sqm', total_area);
                            frm.set_value('residential_lots', residential_lots);
                            frm.set_value('residential_area_sqm', residential_area);

                            // Save the document to persist changes
                            frm.save().then(() => {
                                frappe.show_alert({
                                    message: __('Lots summary updated: {0} lots, {1} sqm total', [total_lots, total_area]),
                                    indicator: 'green'
                                });
                            });
                        }
                    },
                    error: function(r) {
                        frappe.show_alert({
                            message: __('Failed to refresh lots summary: ' + (r.message || 'Unknown error')),
                            indicator: 'red'
                        });
                    }
                });
            }).addClass('btn-warning'); // Make button more visible with warning color
        }
    }
});
